(function(){
  var fs, liftNode, readFile, read, writeFile, write, appendFile, append;
  fs = require('fs');
  liftNode = require('pinky-for-fun').liftNode;
  readFile = liftNode(fs.readFile);
  read = curry$(function(options, pathName){
    return readFile(pathName, options);
  });
  writeFile = liftNode(fs.writeFile);
  write = curry$(function(options, pathName, data){
    return writeFile(pathName, data, options);
  });
  appendFile = liftNode(fs.appendFile);
  append = curry$(function(options, pathName, data){
    return appendFile(pathName, data, options);
  });
  module.exports = {
    read: read,
    write: write,
    append: append
  };
  function curry$(f, bound){
    var context,
    _curry = function(args) {
      return f.length > 1 ? function(){
        var params = args ? args.concat() : [];
        context = bound ? context || this : this;
        return params.push.apply(params, arguments) <
            f.length && arguments.length ?
          _curry.call(context, params) : f.apply(context, params);
      } : f;
    };
    return _curry();
  }
}).call(this);
