(function(){
  var pinky, all, lift, liftNode, slice$ = [].slice;
  pinky = require('pinky');
  all = require('pinky-combinators').all;
  lift = function(f){
    return function(){
      var args;
      args = slice$.call(arguments);
      return all(args).then(function(as){
        return f.apply(null, as);
      });
    };
  };
  liftNode = function(f){
    return function(){
      var args, promise;
      args = slice$.call(arguments);
      promise = pinky();
      all(args).then(function(as){
        return f.apply(null, slice$.call(as).concat([function(err, data){
          switch (false) {
          case !err:
            return promise.reject(err);
          default:
            return promise.fulfill(data);
          }
        }]));
      });
      return promise;
    };
  };
  module.exports = {
    lift: lift,
    liftNode: liftNode
  };
}).call(this);
